<?php/** * Created by PhpStorm. * User: leon-pc * Date: 11/16/2016 * Time: 10:05 AM */class Payment_1{   public $ID,$supplier,$descr,$amount,$dept,$inv_num,$type,$date,$ref;    public function create_from_id($id)    {        require (REQUIRE_CONNECTION);        $query = "             SELECT                  *            FROM purchases             WHERE ID = '" . $id . "'        ";                 try {            $stmt   = $db->prepare($query);            //$stmt1 = $db->prepare($query1);            $result = $stmt->execute();            //$result1 = $stmt1->execute();        }        catch (PDOException $ex) {            die ($ex);            return false;        }        $data = $stmt->fetch();        $this->ID = $data['ID'];        $this->supplier = $data['supplier'];        $this->descr = $data['descr'];        $this->amount = $data['amount'];        $this->dept = $data['department'];        $this->inv_num = $data['inv_num'];                if($data['type'] == 0)            $this->type = "Cheque";        else if($data['type'] == 1)            $this->type = "Cash";        else if($data['type'] == 2)            $this->type = "Bank Transfer";        else if($data['type'] == 3)            $this->type = "Cash";                $this->ref = $data['ref_num'];        $this->date = $data['date'];                       //$data3 = $stmt3->fetch();            return true;                }      }