e = $data['name'];            $this->course_id = $data['course_id'];            $this->student_id = $data['student_id'];                        $this->teacher_id = $data['teacher_id'];                        $this->Test1 = $data['Test1'];                        $this->Test2 = $data['Test2'];                        $this->Exam = $data['Exam'];                        //$this->Term = $data['term'];            return true;        }        else            return false;    }}