<?php/** * Created by PhpStorm. * User: leon-pc * Date: 11/16/2016 * Time: 10:05 AM */class User{   public $ID,$display_name,$user_name,$pass_word,$date_created,$phone_number,$access,$subject1,$subject2,$subject3,$subject4,$subject5;    public function create_using_username($user_name)    {        require (REQUIRE_CONNECTION);        $query = "             SELECT                  *            FROM users             WHERE username = '" . $user_name . "'        ";        try {            $stmt   = $db->prepare($query);            $result = $stmt->execute();        }        catch (PDOException $ex) {            echo $ex;            return false;        }        $data = $stmt->fetch();        if(isset($data) && isset($data['ID']))        {            $this->ID = $data['ID'];            $this->display_name = $data['display_name'];            $this->phone_number = $data['phone_number'];            $this->pass_word = $data['password'];            $this->user_name = $data['username'];            $this->date_created = $data['date_created'];                        $this->access = $data['access'];                        $this->subject1 = $data['subject1'];                        $this->subject2 = $data['subject2'];                        $this->subject3 = $data['subject3'];                        $this->subject4 = $data['subject4'];                        $this->subject5 = $data['subject5'];                        return true;        }        else            return false;    }    public function create_from_id($id)    {        require (REQUIRE_CONNECTION);        $query = "             SELECT                  *            FROM users             WHERE ID = '" . $id . "'        ";        try {            $stmt   = $db->prepare($query);            $result = $stmt->execute();        }        catch (PDOException $ex) {            echo $ex;            return false;        }        $data = $stmt->fetch();        if(isset($data) && isset($data['ID']))        {            $this->ID = $data['ID'];            $this->display_name = $data['display_name'];            $this->phone_number = $data['phone_number'];            $this->pass_word = $data['password'];            $this->user_name = $data['username'];            $this->date_created = $data['date_created'];                        $this->access = $data['access'];                        $this->subject1 = $data['subject1'];                        $this->subject2 = $data['subject2'];                        $this->subject3 = $data['subject3'];                        $this->subject4 = $data['subject4'];                        $this->subject5 = $data['subject5'];            return true;        }        else            return false;    }    function getUserUrl()    {        return url_users($this->ID);    }}